skeletonStyle:
html,
    body,
    .i-website-app__container,
    .i-website-skeleton__section {
      height: 100%;
      margin: 0;
      padding: 0;
    }
    html.pc .i-website-skeleton__header,
    html.mobile .i-website-skeleton__header {
      height: 60px;
      border-bottom: 1px solid #dcdfe6;
    }
    html.pc .i-website-skeleton__section {
      display: flex;
      flex-direction: column;
    }
    html.pc .i-website-skeleton__main {
        flex: 1;
        margin: 20px;
    }
    html.pc .i-website-skeleton__footer,
    html.mobile .i-website-skeleton__footer {
      height: 50px;
      border-top: 1px solid #dcdfe6;
      color: #909399;
      line-height: 50px;
      text-align: center;
      font-size: 14px;
    }
    html.mobile .i-website-skeleton__main,
    html.pc .loading.i-website-skeleton__main,
    html.pc .loading.i-website-skeleton__header,
    html.pc .loading.i-website-skeleton__footer {
      background-color: #ededed;
      background: linear-gradient(100deg, rgba(255, 255, 255, 0) 40%, rgba(255, 255, 255, 0.5) 50%, rgba(255, 255, 255, 0) 60%) #ededed;
      background-size: 200% 100%;
      background-position-x: 180%;
      animation: 1s loading ease-in-out infinite;
    }
    html.mobile .i-website-skeleton__section {
      display: flex;
      flex-direction: column;
    }
    html.mobile .i-website-skeleton__main {
      flex: 1;
      margin: 20px;
    }
    @keyframes loading {
      to {
        background-position-x: -20%;
      }
    }
