skeletonStyle:
html,
    body,
    .i-website-app__container,
    .i-website-skeleton__section {
      height: 100%;
      margin: 0;
      padding: 0;
    }
    .i-website-skeleton__section {
      display: flex;
      flex-direction: row;
      margin-bottom: 20px;
      overflow: hidden;
    }
    html.pc .i-website-skeleton__main {
      flex: 1;
      margin: 20px;
    }
    html.mobile .i-website-skeleton__main,
    html.pc .loading.i-website-skeleton__main,
    html.mobile .i-website-skeleton__section {
      display: flex;
      flex-direction: column;
    }
    html.mobile .i-website-skeleton__main {
      flex: 1;
      margin: 20px;
    }
    html.mobile .i-website-skeleton__main,
    html.pc .loading.i-website-skeleton__main {
      background-color: #ededed;
      background: linear-gradient(100deg, rgba(255, 255, 255, 0) 40%, rgba(255, 255, 255, 0.5) 50%, rgba(255, 255, 255, 0) 60%) #ededed;
      background-size: 200% 100%;
      background-position-x: 180%;
      animation: 1s loading ease-in-out infinite;
    }
    @keyframes loading {
      to {
        background-position-x: -20%;
      }
    }
